package Week1.Day2;

public class LearnIfElseStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int m=45;
		 if (m<20) 
			System.out.println("C");
			
			else if (m<40) {
				System.out.println("B");
				}
				else if (m<50) {
					System.out.println("A");
					}
					else {
						System.out.println("O");
					}
				}
								
	}
